var lazy = $('').lazyRule(() => {
    try {
        var html = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1])
            var url = html.url
            if (html.encrypt == '1') {
                url = unescape(url);
            } else if (html.encrypt == '2') {
                url = unescape(base64Decode(url));
            }
            if (/m3u8|mp4/.test(url)) {
                return url
            } else {
                return 'video://' + input
            }
    } catch (e) {
        return 'video://' + input
    }
})

