//简介
    setDesc: function(desc, num) {
        desc = desc.constructor == Array ? desc.join('<br>') : desc;
        if (desc.replace(/(<br>|\s+)/g, '').length == 0) {
            return
        }
        var info=storage0.getMyVar('er_info','{}')
        if(info['desc']!=undefined){
          info['desc']=info['desc']+' 简介:'+desc;
          storage0.putMyVar('er_info',info);
        }

        const mark = 'desc';
        num = typeof(num) == 'undefined' ? 90 : num
        desc = desc.startsWith('　　') ? desc : '　　' + desc;
        desc = desc.replace(/'/g, "&#39;")

        function substr(str, maxLength) {
            let len = 0;
            for (let i = 0; i < str.length; i++) {
                if (str.charCodeAt(i) > 255) {
                    len += 2;
                } else {
                    len++;
                }
                if (len > maxLength) {
                    return str.slice(0, i) + '...';
                }
            }
            return str;
        }
        let sdesc = substr(desc, num);
        var lazy = $(`#noLoading#`).lazyRule((dc, sdc, m) => {
            var show = storage0.getItem(m, '0');
            var title = findItem('desc').title;
            var re = /(<\/small><br>.*?>).+/g;
            var exp = '展开:';
            var ret = '收起:';
            if (show == '1') {
                updateItem('desc', {
                    title: title
                        .replace(ret, exp)
                        .replace(re, '$1' + sdc + '</small>')
                        .replace(/(<\/small><br><font color=").*?(">)/, '$1#8A2BE2$2')//收起颜色

                })
                storage0.setItem(m, '0');
            } else {
                updateItem('desc', {
                    title: title
                        .replace(exp, ret)
                        .replace(re, '$1' + dc + '</small>')
                        .replace(/(<\/small><br><font color=").*?(">)/, '$1#008000$2')//展开颜色
                })
                storage0.setItem(m, '1');
            }
            return `hiker://empty`
        }, desc, sdesc, mark)
        var sc = storage0.getItem(mark, '0') == '0' ? '展开:' : '收起:';
        var dc = storage0.getItem(mark, '0') == '0' ? sdesc : desc;
        d.push({
        title: '' + '<b><font color="#098AC1">∷  剧情简介	</font></b>' + "<small><a style='text-decoration: none;' href='" + lazy + "'>" + sc + '</a></small><br><font color="#8A2BE2">' + `${dc}` + '</small>',//默认颜色
            col_type: 'rich_text',
            extra: {
                id: 'desc',
                lineSpacing: 6,
                textSize: 15,
                lineVisible: true,
            }
        })
    },

//海报
    strConvert: function(arr) {
        return arr.map(x => {
            str = x;
            if (/(导演|主演|类型)：/.test(str)) {
                str = str.split('/').map((val, index) => (index === 0 ? '' : '') + val).slice(0, 5).join(' ').replace(/ $/, '');
            }
            return str
        })
    },
    setContent: function(obj) {
        var contents = obj.main;
        var sortOrder = obj.sort;
        var img = obj.img;
        var texts = contents.map(x => pdfh(x, 'Text'));
        var array = texts.sort((a, b) => {
            const indexA = sortOrder.indexOf(a.substr(0, 2));
            const indexB = sortOrder.indexOf(b.substr(0, 2));
            return indexA - indexB;
        })
        var regex = new RegExp('(' + sortOrder.join('|') + ')');
        array = array.filter(function(item) {
            return regex.test(item);
        });
        array = this.strConvert(array)
        var titles = [];
        var descs = [];
        switch (array.length) {
            case 4:
            case 5:
                titles = array.slice(0, 2);
                descs = array.slice(2);
                break;
            case 6:
                titles = array.slice(0, 3);
                descs = array.slice(3);
                break;
        }
        storage0.putMyVar('er_info',{
          desc:descs.join('\n'),
          img:img,
        })
        d.push({
            //title: titles[0] + '\n' + this.small(titles.slice(1, 3).join('\n')),
     title:  this.small(titles[0] + '\n' + titles.slice(1, 3).join('\n'),'red'),
           desc: this.small(descs.join('\n'),'#FF7F00'),
     //desc: this.small(pdfh(html, '.context&&.widget&&Text'),'#FF7F00'),
            pic_url: img + '@Referer=',
            url: MY_URL,
            col_type: 'movie_1_vertical_pic_blur',
        })
        if (obj.desc != undefined) {
            this.setDesc(obj.desc);
        }
    },
    setCustom: function(obj, img) {
        var array = [];
        if (obj.constructor == Array) {
            array = obj;
        }
        array = this.strConvert(array);
        var titles = [];
        var descs = [];
        switch (array.length) {
            case 4:
            case 5:
                titles = array.slice(0, 2);
                descs = array.slice(2);
                break;
            case 6:
                titles = array.slice(0, 3);
                descs = array.slice(3);
                break;
        }
        storage0.putMyVar('er_info',{
          desc:descs.join('\n'),
          img:img,
        })
        d.push({
            title: titles[0] + '\n' + this.small(titles.slice(1, 3).join('\n')),
